awk 'function hextodec(str,ret,n,i,k,c){ret=0;n=length(str);for(i=1;i<=n;i++){c=tolower(substr(str,i,1));k=index("123456789abcdef",c);ret=ret*16+k;}return ret} function getip(str,ret){ret=hextodec(substr(str,length(str)-1,2));for(i=5;i>0;i-=2){ret=ret"."hextodec(substr(str,i,2));}return ret} NR>1{dst=getip($2);gw=getip($3);mask=getip($8);printf "%-15s %-15s %-15s %s\n", dst, gw, mask, $1}' /proc/net/route
